import './ModalWithForm.css';
import closeButton from '../../images/Group 119.svg';
import React from 'react';

function ModalWithForm(props) {
    const [closeModal, setCloseModal] = React.useState(false);

    React.useEffect(() => {
        function handleClick(evt) {
            evt.preventDefault();
            setCloseModal(true);
        }

        document.querySelector('.modal__close-button').addEventListener('click', handleClick);
        document.querySelector('.modal').addEventListener('keydown', (evt) => {
            if(evt.key==='Escape'){
                handleClick();
            }
        })

        return () => {
            document.querySelector('.modal__close-button').removeEventListener('click', handleClick);
        };
    });

    return (
        <div className={`modal modal_type_${props.name} ${closeModal ? '' : 'modal_opened'}`}>
            <div class="modal__container">
                 <p>{props.title}</p>

                <form>
                    {props.children}

                    <button type="submit" className="modal__button">{props.buttonText}</button>

                    <button className="modal__close-button"><img src={closeButton}></img></button>
                </form>
            </div>
        </div>
    )
}

export default ModalWithForm;
