import './ModalWithForm.css';
import closeButton from '../../images/Group 119.svg';

const closedModal = false;

function handleOnClick() {
    closedModal = !closedModal;
}

function ModalWithForm(props) {
    const [closeModal, setCloseModal] = React.useState(false);

    return (
        <div className={`modal modal_type_${props.name} ${closedModal?' ':'modal_opened'}`}>
            <div class="modal__container">
                <p>{props.title}</p>

                <form>
                    {props.children}

                    <button type="submit" className="modal__button">{props.buttonText}</button>

                    <button className="modal__close-button" onClose={handleOnClick}><img src={closeButton}></img></button>
                </form>
            </div>
        </div>
    )
}

export default ModalWithForm;
