import './ItemModal.css';
import tShirt from '../../images/T-Shirt.png';
import closeButton from '../../images/Group 119.png';
import React from 'react';

function ItemModal() {
    const [closeModal, setCloseModal] = React.useState(false);

    function handleClick(evt) {
        evt.preventDefault();
        setCloseModal(true);
    }

    function handleEscClose(evt) {
        if(evt.key === 'Escape') {
            handleClick(evt);
        }
    }

    function handleRemoteClick(evt){
        if (evt.target === evt.currentTarget) { 
            handleClick(evt);
        }
    }

    return (
        <div className={`modal-image ${closeModal ? '' : 'modal-image_opened'}`}>
            <div className="modal-image__container" onMouseDown={handleRemoteClick} onKeyDown={handleEscClose}>
                <img src={tShirt} className="modal-image__image"></img>
                <p className="modal-image__item-name">Cap</p>
                <p className="modal-image__weather">Weather: <span>Hot</span></p>

                <button className="modal-image__close-button" onClick={handleClick}><img src={closeButton}></img></button>
            </div>
        </div>
    )
}

export default ItemModal;
