import './ItemCard.css';
import t_shirt from '../../images/T-Shirt.png'

function ItemCard(props) {
    function handleItemCardClick() {
        document.querySelector('.modal-image').classList.add('modal_opened');
        document.querySelector('.modal-image__title').querySelector('.modal-image__title').textContent = props.item;
    }

    return (
        <li className="item-card" onClick={handleItemCardClick}>
            <img src={t_shirt} alt="item_picture" className="item-card__image"></img>
            <p className="item-card__title">{props.item}</p>
        </li>
    )
}


export default ItemCard;
