import './ModalWithForm.css';
import closeButton from '../../images/Group 119.svg';
import React from 'react';

function ModalWithForm(props) {
    const [closeModal, setCloseModal] = React.useState(false);

    function handleClick(evt) {
        evt.preventDefault();
        setCloseModal(true);
    }

    function handleRemoteClick(evt){
        if (evt.target === evt.currentTarget) { 
            handleClick(evt);
        }
    }

    React.useEffect(() => {
        function handleEscClose(evt) {
            if(evt.key === 'Escape') {
                setCloseModal(true);    
            }
        }

        document.addEventListener('keydown', handleEscClose);
    })

    return (
        <div onMouseDown={handleRemoteClick} className={`modal modal_type_${props.name} ${closeModal ? '' : 'modal_opened'}`}>
            <div class="modal__container">
                 <p>{props.title}</p>

                <form>
                    {props.children}

                    <button type="submit" className="modal__button">{props.buttonText}</button>

                    <button className="modal__close-button" onClick={handleClick}><img src={closeButton}></img></button>
                </form>
            </div>
        </div>
    )
}

export default ModalWithForm;
